import Slider from 'react-slick';
import PropTypes from 'prop-types';
import '../styles/Carousel.css';

function CarouselDrinks({ items }) {
  const settings = {
    dots: false,
    arrows: true,
    infinite: false,
    speed: 500,
    slidesToShow: 2,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 1,
        },
      },
    ],
  };

  return (
    <Slider { ...settings }>
      {items.map((item) => (
        <div key={ item.idDrink }>
          <img src={ item.strDrinkThumb } alt={ item.strDrink } />
          <h5>{item.strDrink}</h5>
        </div>
      ))}
    </Slider>
  );
}

CarouselDrinks.propTypes = {
  items: PropTypes.arrayOf(),
}.isRequired;

export default CarouselDrinks;
