import React, { useEffect, useState } from 'react';
import { useHistory, useLocation, useParams } from 'react-router-dom';
import RecipesDetailsMeals from '../components/RecipesDetailsMeals';
import RecipesDetailsDrinks from '../components/RecipesDetailsDrinks';

export default function RecipeDetails() {
  const location = useLocation();
  const history = useHistory();
  const { id } = useParams();
  const [isProgress, setIsProgress] = useState(false);

  useEffect(() => {
    const recipe = JSON.parse(localStorage.getItem('inProgressRecipes'));
    if (recipe) setIsProgress(true);
  }, [id, setIsProgress]);

  return (
    <main>
      { location.pathname === `/meals/${id}` && <RecipesDetailsMeals /> }
      { location.pathname === `/drinks/${id}` && <RecipesDetailsDrinks /> }
      <button
        className="btn-start"
        type="button"
        data-testid="start-recipe-btn"
      >
        { isProgress ? 'Continue Recipe' : 'Start Recipe'}
      </button>
    </main>
  );
}
