import React, { useEffect, useState } from 'react';
import { fetchMeals } from '../services/fetchApi';

export default function CardDrinks() {
  const [mealsApi, setMealsApi] = useState([]);

  useEffect(() => async () => {
    const fetch = await fetchMeals();
    setMealsApi(fetch);
  });

  return (
    <div>
      {
        mealsApi.map((meal, index) => (
          <div
            className="card-recipe"
            key={ meal.idDrink }
            data-testid={ `${index}-recipe-card` }
          >
            <img
              data-testid={ `${index}-card-img` }
              src={ meal.strDrinkThumb }
              alt="Drink"
            />
            <h3 data-testid={ `${index}-card-name` }>{ meal.strDrink }</h3>
          </div>
        ))
      }
    </div>
  );
}
