import React, { useEffect, useState } from 'react';

export default function CardDrinks() {
  const [drinksApi, setDrinksApi] = useState([]);
  const [category, setCategory] = useState([]);

  useEffect(() => {
    const fetchDrinks = async () => {
      const NUMBER = 12;
      const response = await fetch('https://www.thecocktaildb.com/api/json/v1/1/search.php?s=');
      const api = await response.json();
      const data = api.drinks;
      setDrinksApi(data.slice(0, NUMBER));
    };
    fetchDrinks();

    const fetchCategory = async () => {
      const NUMBER = 5;
      const response = await fetch('https://www.themealdb.com/api/json/v1/1/list.php?c=list');
      const api = await response.json();
      const data = api.drinks;
      setCategory(data.slice(0, NUMBER));
    };
    fetchCategory();
  }, [setDrinks, setCategory]);

  return (
    <div>
      {
        category.map((list) => (
          <button
            data-testid={ `${list.strCategory}-category-filter` }
            key={ list.strCategory }
          >
            { list.strCategory }
          </button>
        ))
      }
      {
        drinksApi.map((meal, index) => (
          <div
            className="card-recipe"
            key={ meal.idDrink }
            data-testid={ `${index}-recipe-card` }
          >
            <img
              data-testid={ `${index}-card-img` }
              src={ meal.strDrinkThumb }
              alt="Drink"
            />
            <h3 data-testid={ `${index}-card-name` }>{ meal.strDrink }</h3>
          </div>
        ))
      }
    </div>
  );
}
