import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';

export default function CardDrinks() {
  const history = useHistory();
  const [drinks, setDrinks] = useState([]);
  const [category, setCategory] = useState([]);
  const [initialDrinks, setInitialDrinks] = useState([]);
  const [filter, setFilter] = useState('');

  useEffect(() => {
    const fetchDrinks = async () => {
      const NUMBER = 12;
      const response = await fetch('https://www.thecocktaildb.com/api/json/v1/1/search.php?s=');
      const api = await response.json();
      const data = api.drinks;
      setDrinks(data.slice(0, NUMBER));
    };
    fetchDrinks();

    const fetchCategory = async () => {
      const NUMBER = 5;
      const response = await fetch('https://www.thecocktaildb.com/api/json/v1/1/list.php?c=list');
      const api = await response.json();
      const data = api.drinks;
      setCategory(data.slice(0, NUMBER));
    };
    fetchCategory();
  });

  const handleFilter = async (meal) => {
    if (filter === meal) {
      setDrinks(initialDrinks);
    } else {
      const NUMBER = 12;
      const response = await fetch(`https://www.themealdb.com/api/json/v1/1/filter.php?c=${meal}`);
      const api = await response.json();
      const data = api.Drinks;
      setDrinks(data.slice(0, NUMBER));
      setFilter(meal);
    }
  };

  const handleAllDrinks = async () => {
    setDrinks(initialDrinks);
    setFilter([]);
  };

  const handleCard = (id) => {
    if (drinks.length > 1) {
      history.push(`/drinks/${id}`);
    }
  };

  return (
    <div>
      {
        category.map((list) => (
          <button
            onClick={ () => handleFilter(list.strCategory) }
            data-testid={ `${list.strCategory}-category-filter` }
            key={ list.strCategory }
          >
            { list.strCategory }
          </button>
        ))
      }
      <button
        onClick={ handleAllDrinks }
        data-testid="All-category-filter"
        type="button"
      >
        All
      </button>
      {
        drinks.map((meal, index) => (
          <div
            className="card-recipe"
            key={ meal.idDrink }
            data-testid={ `${index}-recipe-card` }
          >
            <img
              data-testid={ `${index}-card-img` }
              src={ meal.strDrinkThumb }
              alt="Drink"
            />
            <h3 data-testid={ `${index}-card-name` }>{ meal.strDrink }</h3>
            <button
              type="button"
              onClick={ () => handleCard(meal.idDrink) }
            >
              Detalhes
            </button>
          </div>
        ))
      }
    </div>
  );
}
