import React, { useEffect, useMemo, useState } from 'react';
import PropTypes from 'prop-types';
import MyContext from './AppContext';

const alert = 'Sorry, we haven\'t found any recipes for these filters.';

export default function Provider({ children }) {
  const [search, setSearch] = useState('');
  const [radioType, setRadioType] = useState('');
  const [meals, setMeals] = useState([]);
  const [drinks, setDrinks] = useState([]);
  const [categoryMeal, setCategoryMeal] = useState([]);
  const [categoryDrink, setCategoryDrink] = useState([]);

  useEffect(() => {
    const fetchMeals = async () => {
      const NUMBER = 12;
      const response = await fetch('https://www.themealdb.com/api/json/v1/1/search.php?s=');
      const data = await response.json();
      const newData = data.meals;
      setMeals(newData.slice(0, NUMBER));
      if (data.length === 0 || data.length === null) {
        global.alert(alert);
      }
    };
    fetchMeals();
    const fetchCategoryMeal = async () => {
      try {
        const NUMBER = 5;
        const response = await fetch('https://www.themealdb.com/api/json/v1/1/list.php?c=list');
        const data = await response.json();
        const newData = data.meals;
        setCategoryMeal(newData.slice(0, NUMBER));
      } catch (error) {
        console.error(error);
      }
    };
    fetchCategoryMeal();
    const fetchDrinks = async () => {
      const NUMBER = 12;
      const response = await fetch('https://www.thecocktaildb.com/api/json/v1/1/search.php?s=');
      const data = await response.json();
      const newData = data.drinks;
      setDrinks(newData.slice(0, NUMBER));
      if (data.length === 0 || data.length === null) {
        global.alert(alert);
      }
    };
    fetchDrinks();
    const fetchCategoryDrink = async () => {
      try {
        const NUMBER = 5;
        const response = await fetch('https://www.thecocktaildb.com/api/json/v1/1/list.php?c=list');
        const data = await response.json();
        const newData = data.drinks;
        setCategoryDrink(newData.slice(0, NUMBER));
      } catch (error) {
        console.error(error);
      }
    };
    fetchCategoryDrink();
  }, [setMeals, setDrinks]);

  const value = useMemo(() => ({
    meals,
    setMeals,
    drinks,
    setDrinks,
    search,
    setSearch,
    radioType,
    setRadioType,
    categoryMeal,
    setCategoryMeal,
    categoryDrink,
    setCategoryDrink,
  }), [
    meals,
    setMeals,
    drinks,
    setDrinks,
    search,
    setSearch,
    radioType,
    setRadioType,
    categoryMeal,
    setCategoryMeal,
    categoryDrink,
    setCategoryDrink,
  ]);

  return (
    <MyContext.Provider value={ value }>
      { children }
    </MyContext.Provider>
  );
}

Provider.propTypes = {
  children: PropTypes.node.isRequired,
};
