import React from 'react';
import Slider from 'react-slick';

const cards = [
  { title: 'Card 1', image: 'https://picsum.photos/id/1015/300/200' },
  { title: 'Card 2', image: 'https://picsum.photos/id/1016/300/200' },
  { title: 'Card 3', image: 'https://picsum.photos/id/1018/300/200' },
  { title: 'Card 4', image: 'https://picsum.photos/id/1019/300/200' },
  { title: 'Card 5', image: 'https://picsum.photos/id/1020/300/200' },
  { title: 'Card 6', image: 'https://picsum.photos/id/1021/300/200' },
];

const settings = {
  dots: true,
  infinite: true,
  speed: 500,
  slidesToShow: 2,
  slidesToScroll: 2,
};

function Carousel() {
  return (
    <Slider { ...settings }>
      {cards.map((card, index) => (
        <div>
          <img src={ image } alt={ title } />
          <h3>{title}</h3>
        </div>
      ))}
    </Slider>
  );
}

export default Carousel;
