import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import clipboardCopy from 'clipboard-copy';
import shareIcon from '../images/shareIcon.svg';
import noFavoriteIcon from '../images/whiteHeartIcon.svg';
import favoriteIcon from '../images/blackHeartIcon.svg';

const ingredients = [];

export default function RecipesInProgressDrink() {
  const { id } = useParams();
  const [drinks, setDrinks] = useState([]);
  const [copied, setCopied] = useState(false);
  const [favoriteRecipe, setFavoriteRecipe] = useState([]);
  const [isFavorite, setIsFavorite] = useState(false);

  useEffect(() => {
    const fetchDrinks = async () => {
      const response = await fetch(`https://www.thecocktaildb.com/api/json/v1/1/lookup.php?i=${id}`);
      const api = await response.json();
      const data = api.drinks;

      if (data) {
        const NUMBER = 13;
        for (let index = 1; index <= NUMBER; index += 1) {
          const measure = data[0][`strMeasure${index}`];
          const ingredient = data[0][`strIngredient${index}`];
          ingredients.push(
            `${measure} ${ingredient}`,
          );
        }
      }
      setDrinks(data);
    };
    fetchDrinks();
  }, [setDrinks, id]);

  return (
    <div data-testid="drink-details">
      <button type="button" data-testid="share-btn">
        <img src={ shareIcon } alt="Share Icon" />
      </button>
      <button type="button" data-testid="favorite-btn">
        <img src={ favoriteIcon } alt="Favorite Icon" />
      </button>
      {
        drinks.map((drink) => (
          <div key={ drink.idDrink }>
            <img
              width="300px"
              data-testid="recipe-photo"
              src={ drink.strDrinkThumb }
              alt={ drink.strDrink }
            />
            <h3 data-testid="recipe-title">{ drink.strDrink }</h3>
            <h4 data-testid="recipe-category">{ drink.strAlcoholic }</h4>
            <p />
            <h5>Ingredients</h5>
            <ul>
              {
                ingredients.map((ingredient, index) => (
                  <li
                    data-testid={ `${index}-ingredient-name-and-measure` }
                    key={ index }
                  >
                    { ingredient }
                  </li>
                ))
              }
            </ul>
            <p data-testid="instructions">{ drink.strInstructions }</p>
          </div>
        ))
      }
    </div>
  );
}
