import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import getYoutubeEmbedUrl from '../utils/getYoutubeEmbedUrl';

export default function RecipesDetailsMeals() {
  const { id } = useParams();
  const [meal, setMeal] = useState({});
  const [recommendation, setRecommendation] = useState([]);

  useEffect(() => {
    const fetchMeals = async () => {
      const response = await fetch(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${id}`);
      const api = await response.json();
      const data = api.meals;
      setMeals(data[0]);
    };
    fetchMeals();

    const fetchRecommendation = async () => {
      const NUMBER = 6;
      const response = await fetch('https://www.thecocktaildb.com/api/json/v1/1/search.php?s=');
      const api = await response.json();
      const data = api.drinks;
      setRecommendation(data.slice(0, NUMBER));
    };
    fetchRecommendation();
  }, [setMeal, setRecommendation, id]);

  const renderIngredients = () => {
    const NUMBER = 13;
    const ingredients = [];
    for (let index = 1; index <= NUMBER; index += 1) {
      if (meals[0][`strIngredient${index}`]) {
        ingredients.push(
          `${meals[0][`strMeasure${index}`]} ${meals[0][`strIngredient${index}`]}`,
        );
      }
    }
    return ingredients.map((ingredient, index) => (
      <li
        data-testid={ `${index}-ingredient-name-and-measure` }
        key={ index }
      >
        { ingredient }

      </li>
    ));
  };

  return (
    <main>
      {/* {
        meals.map((meal) => (
          <div key={ meal.idMeal }>
            <img
              data-testid="recipe-photo"
              src={ meal.strMealThumb }
              alt={ meal.strMeal }
            />
            <h3 data-testid="recipe-title">{ meal.strMeal }</h3>
            <h4 data-testid="recipe-category">{ meal.strCategory }</h4>
            <h5>Ingredients</h5>
            <ul>{ renderIngredients() }</ul>
            <p data-testid="instructions">{ meal.strInstructions }</p>
            <iframe
              data-testid="video"
              title={ meal.strMeal }
              width="560"
              height="315"
              src={ getYoutubeEmbedUrl(meal.strYoutube) }
            />
          </div>
        ))
      } */}
    </main>
  );
}
