import Slider from 'react-slick';
import PropTypes from 'prop-types';
import '../styles/Carousel.css';

function CarouselDrinks({ items }) {
  const settings = {
    dots: false,
    arrows: true,
    infinite: false,
    speed: 500,
    slidesToShow: 2,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 1,
        },
      },
    ],
  };

  return (
    <section className="recommend">
      <Slider { ...settings }>
        <h4>Recommendations</h4>
        <div className="slide">
          <img
            className="img-slide"
            src={ items[0].strMealThumb }
            alt={ items[0].strMeal }
          />
          <h5>{items[0].strMeal}</h5>
        </div>

        <div className="slide">
          <img
            className="img-slide"
            src={ items[1].strMealThumb }
            alt={ items[1].strMeal }
          />
          <h5>{items[1].strMeal}</h5>
        </div>
      </Slider>
    </section>
  );
}

CarouselDrinks.propTypes = {
  items: PropTypes.arrayOf(),
}.isRequired;

export default CarouselDrinks;
