import React, { useContext, useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import SlideDrinks from './SlideDrinks';
import AppContext from '../contexts/AppContext';

const ingredients = [];

export default function RecipesDetailsMeals() {
  const { favoriteRecipes, setFavoriteRecipes,
    inProgressRecipes, setInProgressRecipes } = useContext(AppContext);
  const { id } = useParams();
  const [meals, setMeals] = useState([]);

  useEffect(() => {
    const fetchMeals = async () => {
      const response = await fetch(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${id}`);
      const api = await response.json();
      const data = api.meals;

      if (data) {
        const NUMBER = 13;
        for (let index = 1; index <= NUMBER; index += 1) {
          const measure = data[0][`strMeasure${index}`];
          const ingredient = data[0][`strIngredient${index}`];
          ingredients.push(
            `${measure} ${ingredient}`,
          );
        }
      }
      setMeals(data);
    };

    fetchMeals();
  }, [setMeals, id]);

  useEffect(() => {
    const savedFavorites = JSON.parse(localStorage.getItem('favoriteRecipes'));
    if (savedFavorites) {
      setFavoriteRecipes(savedFavorites);
    }

    const savedProgress = JSON.parse(localStorage.getItem('inProgressRecipes'));
    if (savedProgress) {
      setInProgressRecipes(savedProgress);
    }
  }, [setInProgressRecipes, setFavoriteRecipes]);

  useEffect(() => {
    localStorage.setItem('favoriteRecipes', JSON.stringify(favoriteRecipes));
    localStorage.setItem('inProgressRecipes', JSON.stringify(inProgressRecipes));
  }, [favoriteRecipes, inProgressRecipes]);

  return (
    <div data-testid="meal-details">
      {
        meals.map((meal) => (
          <div key={ meal.idMeal }>
            <img
              width="300px"
              data-testid="recipe-photo"
              src={ meal.strMealThumb }
              alt={ meal.strMeal }
            />
            <h3 data-testid="recipe-title">{ meal.strMeal }</h3>
            <h4 data-testid="recipe-category">{ meal.strCategory }</h4>
            <h5>Ingredients</h5>
            <ul>
              {
                ingredients.map((ingredient, index) => (
                  <li
                    data-testid={ `${index}-ingredient-name-and-measure` }
                    key={ index }
                  >
                    { ingredient }
                  </li>
                ))
              }
            </ul>
            <p data-testid="instructions">{ meal.strInstructions }</p>
            <iframe
              data-testid="video"
              title={ meal.strMeal }
              width="300"
              height="150"
              src={ meal.strYoutube.replace('watch?v=', 'embed/') }
            />
          </div>
        ))
      }
      <SlideDrinks />
    </div>
  );
}
