import React, { useEffect, useMemo, useState } from 'react';
import PropTypes from 'prop-types';
import MyContext from './AppContext';

export default function Provider({ children }) {
  const [search, setSearch] = useState('');
  const [radioType, setRadioType] = useState('');
  const [favoriteRecipe, setFavoriteRecipe] = useState([]);
  const [recipesProgress, setRecipesProgress] = useState({ meals: {}, drinks: {} });

  useEffect(() => {
    const savedInProgress = JSON.parse(localStorage.getItem('inProgressRecipes'));
    if (savedInProgress) {
      setRecipesProgress(savedInProgress);
    }

    const savedFavoriteRecipes = JSON.parse(localStorage.getItem('favoriteRecipes'));
    if (savedFavoriteRecipes) {
      setFavoriteRecipe(savedFavoriteRecipes);
    }
  }, [setRecipesProgress, setFavoriteRecipe]);

  useEffect(() => {
    localStorage.setItem('favoriteRecipes', JSON.stringify(favoriteRecipe));
    localStorage.setItem('inProgressRecipes', JSON.stringify(recipesProgress));
  }, [favoriteRecipe, recipesProgress]);

  const value = useMemo(() => ({
    search,
    setSearch,
    radioType,
    setRadioType,
    recipesProgress,
    setRecipesProgress,
    favoriteRecipe,
    setFavoriteRecipe,
  }), [
    search,
    setSearch,
    radioType,
    setRadioType,
    recipesProgress,
    setRecipesProgress,
    favoriteRecipe,
    setFavoriteRecipe,
  ]);

  return (
    <MyContext.Provider value={ value }>
      { children }
    </MyContext.Provider>
  );
}

Provider.propTypes = {
  children: PropTypes.node.isRequired,
};
