import React from 'react';
import Slider from 'react-slick';

const settings = {
  dots: true,
  infinite: true,
  speed: 500,
  slidesToShow: 2,
  slidesToScroll: 2,
};

function Carousel({ slides }) {
  return (
    <Slider { ...settings }>
      {slides.map((slide) => (
        <div key={ slide.idDrink }>
          <img src={ slide.strDrinkThumb } alt={ slide.strDrink } />
          <h5>{ slide.strDrink }</h5>
        </div>
      ))}
    </Slider>
  );
}

Carousel.propTypes = {
  slides: PropTypes.arrayOf().isRequired,
};

export default Carousel;
