import React, { useContext, useEffect, useState } from 'react';
import { useLocation, useParams } from 'react-router-dom';
import RecipesDetailsMeals from '../components/RecipesDetailsMeals';
import RecipesDetailsDrinks from '../components/RecipesDetailsDrinks';
import AppContext from '../contexts/AppContext';

export default function RecipeDetails() {
  const { favoriteRecipes, setFavoriteRecipes,
    inProgressRecipes, setInProgressRecipes } = useContext(AppContext);
  const location = useLocation();
  const { id } = useParams();
  const [isProgress, setIsProgress] = useState(false);

  useEffect(() => {
    const savedFavorites = JSON.parse(localStorage.getItem('favoriteRecipes'));
    if (savedFavorites) {
      setFavoriteRecipes(savedFavorites);
    }

    const savedProgress = JSON.parse(localStorage.getItem('inProgressRecipes'));
    if (savedProgress) {
      setInProgressRecipes(savedProgress);
    }

    const type = location.pathname.includes('/meals') ? 'meals' : 'drinks';
    const storage = JSON.parse(localStorage.getItem('inProgressRecipes'));
    setIsProgress(!!storage[type][id]);
  }, [id, setIsProgress, location], setFavoriteRecipes, setInProgressRecipes);

  return (
    <main>
      { location.pathname === `/meals/${id}` && <RecipesDetailsMeals /> }
      { location.pathname === `/drinks/${id}` && <RecipesDetailsDrinks /> }
      <button
        className="btn-start"
        type="button"
        data-testid="start-recipe-btn"
      >
        { !isProgress ? 'Start Recipe' : 'Continue Recipe'}
      </button>
    </main>
  );
}
