import React, { useEffect, useState } from 'react';
import Header from '../components/Header';
import blackHeartIcon from '../images/blackHeartIcon.svg';
import shareIcon from '../images/shareIcon.svg';

const SECONDS_NUMBERS = 3000;

export default function Favorites() {
  const [favoriteRecipes, setFavoriteRecipes] = useState([]);
  const [favoriteAllRecipes, setFavoriteAllRecipes] = useState([]);
  const [drinks, setDrinks] = useState([]);
  const [meals, setMeals] = useState([]);

  useEffect(() => {
    const storedFavorites = JSON.parse(localStorage.getItem('favoriteRecipes'));

    if (storedFavorites) {
      setFavoriteRecipes(storedFavorites);
      setFavoriteAllRecipes(storedFavorites);
    }
  }, [setFavoriteAllRecipes, setFavoriteRecipes]);

  useEffect(() => {
    const storedFavorites = JSON.parse(localStorage.getItem('favoriteRecipes'));
    const drinksFilter = storedFavorites.filter((item) => item.type === 'drink');
    setDrinks(drinksFilter);
    const mealsFilter = storedFavorites.filter((item) => item.type === 'meal');
    setMeals(mealsFilter);
  }, [setMeals, setDrinks]);

  function handleRemoveFavorite(index) {
    const newFavorites = [...favoriteRecipes];
    newFavorites.splice(index, 1);
    localStorage.setItem('favoriteRecipes', JSON.stringify(newFavorites));
    setFavoriteRecipes(newFavorites);
  }

  function handleShareRecipe(index) {
    const recipe = favoriteRecipes[index];
    const recipeUrl = `http://localhost:3000/${recipe.type}s/${recipe.id}`;
    navigator.clipboard.writeText(recipeUrl);

    const message = document.createElement('div');
    message.textContent = 'Link copied!';
    message.style.position = 'fixed';
    message.style.top = '10px';
    message.style.right = '10px';
    message.style.padding = '10px';
    message.style.backgroundColor = 'white';
    message.style.border = '1px solid black';

    document.body.appendChild(message);

    setTimeout(() => {
      message.remove();
    }, SECONDS_NUMBERS);
  }

  return (
    <div>
      <Header />
      <div>
        <button
          onClick={ () => setFavoriteRecipes(favoriteAllRecipes) }
          data-testid="filter-by-all-btn"
        >
          All
        </button>
        <button
          onClick={ () => setFavoriteRecipes(meals) }
          data-testid="filter-by-meal-btn"
        >
          Meals

        </button>
        <button
          onClick={ () => setFavoriteRecipes(drinks) }
          data-testid="filter-by-drink-btn"
        >
          Drinks

        </button>
      </div>
      <div>
        {favoriteRecipes.map((recipe, index) => (
          <div key={ recipe.id }>
            <img
              src={ recipe.image }
              alt={ `${recipe.name} recipe` }
              data-testid={ `${index}-horizontal-image` }
            />
            <p data-testid={ `${index}-horizontal-top-text` }>
              {recipe.nationality}
              {' '}
              -
              {' '}
              {recipe.category}
            </p>
            <h3 data-testid={ `${index}-horizontal-name` }>{recipe.name}</h3>
            <button
              data-testid={ `${index}-horizontal-share-btn` }
              onClick={ () => handleShareRecipe(index) }
            >
              <img src={ shareIcon } alt="Share" />
            </button>
            <button
              data-testid={ `${index}-horizontal-favorite-btn` }
              onClick={ () => handleRemoveFavorite(index) }
            >
              <img src={ blackHeartIcon } alt="Remove favorite" />
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
