import React, { useEffect, useState } from 'react';
import { useHistory, useLocation, useParams } from 'react-router-dom';
import RecipesDetailsMeals from '../components/RecipesDetailsMeals';
import RecipesDetailsDrinks from '../components/RecipesDetailsDrinks';

export default function RecipeDetails() {
  const location = useLocation();
  const history = useHistory();
  const { id } = useParams();
  const [isInProgress, setIsInProgress] = useState(false);

  useEffect(() => {
    const savedInProgress = JSON.parse(localStorage.getItem('inProgressRecipes'));
    if (savedInProgress) {
      setIsInProgress(true);
    } else {
      localStorage.setItem('inProgressRecipes', JSON.stringify([]));
    }
  }, [setIsInProgress]);

  const handleClick = () => {
    if (location.pathname === `/meals/${id}`) {
      history.push(`/meals/${id}/in-progress`);
    }

    if (location.pathname === `/drinks/${id}`) {
      history.push(`/drinks/${id}/in-progress`);
    }
  };
  return (
    <main>
      { location.pathname === `/meals/${id}` && <RecipesDetailsMeals /> }
      { location.pathname === `/drinks/${id}` && <RecipesDetailsDrinks /> }
      <button
        className="btn-start"
        type="button"
        data-testid="start-recipe-btn"
        onClick={ handleClick }
      >
        { isInProgress ? 'Continue Recipe' : 'Start Recipe' }
      </button>
    </main>
  );
}
