import Carousel from 'react-bootstrap/Carousel';
import PropTypes from 'prop-types';
import React from 'react';
import '../styles/Slider.css';

function SlideDrinks({ slides }) {
  return (
    <Carousel
      slidesToShow={ 2 }
      activeIndex={ index }
      nextIcon={ <span
        aria-hidden="true"
        className="carousel-control-next-icon changed"
      /> }
    >
      {slides.map((slide) => (
        <Carousel.Item key={ slide.idDrink }>
          <img
            className="d-block w-100"
            src={ slide.strDrinkThumb }
            alt={ slide.strDrink }
          />
          <Carousel.Caption>
            <h3>{slide.strDrink}</h3>
          </Carousel.Caption>
        </Carousel.Item>
      ))}
    </Carousel>
  );
}

SlideDrinks.propTypes = {
  slides: PropTypes.arrayOf(),
}.isRequired;

export default SlideDrinks;
