import React, { Component } from 'react';
import Slider from 'react-slick';

function CarouselDrinks() {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };
  return (
    <div>
      <h2> Single Item</h2>
      <Slider { ...settings }>
        <div className="slide">
          <img
            className="img-slide"
            src={ items[0].strDrinkThumb }
            alt={ items[0].strDrink }
          />
          <h5>{items[0].strDrink}</h5>
        </div>
      </Slider>
    </div>
  );
}
