import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import clipboardCopy from 'clipboard-copy';
import shareIcon from '../images/shareIcon.svg';
import noFavoriteIcon from '../images/whiteHeartIcon.svg';
import favoriteIcon from '../images/blackHeartIcon.svg';

const ingredients = [];

export default function RecipeInProgressMeal() {
  const { id } = useParams();
  const [meals, setMeals] = useState([]);
  const [copied, setCopied] = useState(false);
  // const [favoriteRecipe, setFavoriteRecipe] = useState([]);
  const [isFavorite, setIsFavorite] = useState(false);

  useEffect(() => {
    const fetchMeals = async () => {
      const response = await fetch(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${id}`);
      const api = await response.json();
      const data = api.meals;

      if (data) {
        const NUMBER = 13;
        for (let index = 1; index <= NUMBER; index += 1) {
          const measure = data[0][`strMeasure${index}`];
          const ingredient = data[0][`strIngredient${index}`];
          ingredients.push(
            `${measure} ${ingredient}`,
          );
        }
      }
      setMeals(data);
    };
    fetchMeals();
  }, [setMeals, favoriteRecipe, id]);

  const handleShareClick = async () => {
    const currentURL = window.location.href;
    await clipboardCopy(currentURL);
    setCopied(true);
  };

  const handleFavorite = () => {
    setIsFavorite(!isFavorite);
    if (!isFavorite) {
      const recipe = {
        id: meals[0].idMeal,
        type: 'Meal',
        nationality: meals[0].strArea,
        category: meals[0].strCategory || '',
        alcoholicOrNot: meals[0].strAlcoholic || '',
        name: meals[0].strMeal,
        image: meals[0].strMealThumb,
      };
      localStorage.setItem('favoriteRecipes', JSON.stringify([...recipe]));
    } else {
      const updatedRecipes = favoriteRecipe.filter(
        (recipe) => recipe.id !== meals[0].idMeal,
      );
      setFavoriteRecipe([updatedRecipes]);
    }
  };

  // const handleSelect = (ingredient) => {

  // };

  return (
    <div data-testid="meal-details">
      <button onClick={ handleShareClick } type="button" data-testid="share-btn">
        <img src={ shareIcon } alt="Share Icon" />
      </button>
      <button onClick={ handleFavorite } type="button" data-testid="favorite-btn">
        <img src={ isFavorite ? favoriteIcon : noFavoriteIcon } alt="Favorite Icon" />
      </button>
      {copied && <p>Link copied!</p>}
      {
        meals.map((meal) => (
          <div key={ meal.idMeal }>
            <img
              width="300px"
              data-testid="recipe-photo"
              src={ meal.strMealThumb }
              alt={ meal.strMeal }
            />
            <h3 data-testid="recipe-title">{ meal.strMeal }</h3>
            <h4 data-testid="recipe-category">{ meal.strCategory }</h4>
            <h5>Ingredients</h5>
            {
              ingredients.map((ingredient, index) => (
                <label
                  data-testid={ `${index}-ingredient-name-and-measure` }
                  key={ index }
                  htmlFor={ `check${index}` }
                >
                  <input
                    onClick={ () => handleSelect(ingredient) }
                    type="checkbox"
                    name="checkbox"
                    id={ `check${index}` }
                  />
                  { ingredient }
                </label>
              ))
            }
            <p data-testid="instructions">{ meal.strInstructions }</p>
            <iframe
              data-testid="video"
              title={ meal.strMeal }
              width="300"
              height="150"
              src={ meal.strYoutube.replace('watch?v=', 'embed/') }
            />
          </div>
        ))
      }
    </div>
  );
}
