import React from 'react';
import { useLocation, useParams } from 'react-router-dom';
import RecipeInProgressDrink from '../components/RecipeInProgressDrink';
import RecipeInProgressMeal from '../components/RecipeInProgressMeal';

export default function RecipeInProgress() {
  const location = useLocation();
  const { id } = useParams();
  return (
    <main>
      { location.pathname === `/meals/${id}/in-progress` && <RecipeInProgressMeal /> }
      { location.pathname === `/drinks/${id}/in-progress` && <RecipeInProgressDrink /> }
      <button
        className="btn-start"
        type="button"
        data-testid="start-recipe-btn"
      >
        Continue Recipe
      </button>
    </main>
  );
}
