import React from 'react';
import PropTypes from 'prop-types';
import Slider from 'react-slick';

function CarouselDrinks({ items }) {
  const settings = {
    dots: true,
    infinite: false,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };
  return (
    <div>
      <h2> Single Item</h2>
      <Slider { ...settings }>
        <div>

          {
            items.map((drink) => (
              <div key={ drink.idDrink } className="slide">
                <img
                  className="img-slide"
                  src={ drink.strDrinkThumb }
                  alt={ drink.strDrink }
                />
                <h5>{drink.strDrink}</h5>
              </div>
            ))
          }
        </div>
      </Slider>
    </div>
  );
}

CarouselDrinks.propTypes = {
  items: PropTypes.arrayOf(),
}.isRequired;

export default CarouselDrinks;
