import React, { useContext } from 'react';
import AppContext from '../contexts/AppContext';

export default function SearchBar() {
  const { inputSearch, inputIngredient, inputName,
    inputFirstLetter, setInputSearch, setInputIngredient,
    setInputName, setInputFirstLetter } = useContext(AppContext);

  return (
    <div>
      <input
        type="text"
        placeholder="Search"
        data-testid="search-input"
        value={ inputSearch }
        onChange={ ({ target }) => setInputSearch() }
      />

      <label htmlFor="ingredient">
        Ingredient
        <input
          data-testid="ingredient-search-radio"
          type="radio"
          name="ingredient"
          value={ inputIngredient }
        />
      </label>

      <label htmlFor="name">
        Name
        <input
          data-testid="name-search-radio"
          type="radio"
          name="name"
          value={ inputName }
        />
      </label>

      <label htmlFor="name">
        First Letter
        <input
          data-testid="first-letter-search-radio"
          type="radio"
          name="first-letter"
          value={ inputFirstLetter }
        />
      </label>

      <button type="button" data-testid="exec-search-btn">Search</button>
    </div>
  );
}
