import React from 'react';
import { render, screen, fireEvent } from '@testing-library/react';
import { BrowserRouter } from 'react-router-dom';
import RecipeDetails from './RecipeDetails';

describe('RecipeDetails', () => {
  beforeEach(() => {
    localStorage.clear();
  });

  test('renders recipe details for meals when location matches', () => {
    const id = '123';
    const location = { pathname: `/meals/${id}` };
    render(
      <BrowserRouter>
        <RecipeDetails location={ location } />
      </BrowserRouter>,
    );

    expect(screen.getByTestId('recipes-details-meals')).toBeInTheDocument();
  });

  test('renders recipe details for drinks when location matches', () => {
    const id = '456';
    const location = { pathname: `/drinks/${id}` };
    render(
      <BrowserRouter>
        <RecipeDetails location={ location } />
      </BrowserRouter>,
    );

    expect(screen.getByTestId('recipes-details-drinks')).toBeInTheDocument();
  });

  test('renders start recipe button with correct label when not in progress', () => {
    const id = '123';
    const location = { pathname: `/meals/${id}` };
    render(
      <BrowserRouter>
        <RecipeDetails location={ location } />
      </BrowserRouter>,
    );

    expect(screen.getByTestId('start-recipe-btn')).toHaveTextContent('Start Recipe');
  });

  test('renders start recipe button with correct label when in progress', () => {
    const id = '123';
    const location = { pathname: `/meals/${id}` };
    localStorage.setItem('inProgressRecipes', JSON.stringify({ meals: { [id]: {} } }));
    render(
      <BrowserRouter>
        <RecipeDetails location={ location } />
      </BrowserRouter>,
    );

    expect(screen.getByTestId('start-recipe-btn')).toHaveTextContent('Continue Recipe');
  });

  test(
    'navigates to the correct URL when start recipe button is clicked for meals',
    () => {
      const id = '123';
      const location = { pathname: `/meals/${id}` };
      const historyMock = { push: jest.fn() };
      render(
        <BrowserRouter>
          <RecipeDetails location={ location } history={ historyMock } />
        </BrowserRouter>,
      );

      fireEvent.click(screen.getByTestId('start-recipe-btn'));

      expect(historyMock.push).toHaveBeenCalledWith(`/meals/${id}/in-progress`);
    },
  );

  test('navigates to the correct URL when start recipe button is clicked for drinks', () => {
    const id = '456';
    const location = { pathname: `/drinks/${id}` };
    const historyMock = { push: jest.fn() };
    render(
      <BrowserRouter>
        <RecipeDetails location={ location } history={ historyMock } />
      </BrowserRouter>,
    );

    fireEvent.click(screen.getByTestId('start-recipe-btn'));

    expect(historyMock.push).toHaveBeenCalledWith(`/drinks/${id}/in-progress`);
  });
});
