import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import clipboardCopy from 'clipboard-copy';
import SlideDrinks from './SlideDrinks';
import shareIcon from '../images/shareIcon.svg';
import favoriteIcon from '../images/whiteHeartIcon.svg';
import checkFavoriteIcon from '../images/blackHeartIcon.svg';

const ingredients = [];

export default function RecipesDetailsMeals() {
  const { id } = useParams();
  const [meals, setMeals] = useState([]);
  const [copied, setCopied] = useState(false);
  const [isFavorite, setIsFavorite] = useState(false);

  useEffect(() => {
    const fetchMeals = async () => {
      const response = await fetch(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${id}`);
      const api = await response.json();
      const data = api.meals;

      if (data) {
        const NUMBER = 13;
        for (let index = 1; index <= NUMBER; index += 1) {
          const measure = data[0][`strMeasure${index}`];
          const ingredient = data[0][`strIngredient${index}`];
          ingredients.push(
            `${measure} ${ingredient}`,
          );
        }
      }
      setMeals(data);
    };

    fetchMeals();
  }, [setMeals, id]);

  useEffect(() => {
    const savedRecipes = JSON.parse(localStorage.getItem('favoriteRecipes'));
    if (savedRecipes) {
      setFavoriteRecipe(savedRecipes);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('favoriteRecipes', JSON.stringify(favoriteRecipe));
  }, [favoriteRecipe]);

  const handleShareClick = async () => {
    const currentURL = window.location.href;
    await clipboardCopy(currentURL);
    setCopied(true);
  };

  const handleFavoriteClick = () => {
    setIsFavorite(!isFavorite);

    if (!isFavorite) {
      const newFavoriteRecipe = {
        id: meals[0].strMeal,
        type: 'meals',
        nationality: meals[0].strArea || '',
        category: meals[0].strCategory || '',
        alcoholicOrNot: meals[0].strAlcoholic || '',
        name: meals[0].strMeal,
        image: meals[0].strMealThumb,
      };
      setFavoriteRecipe([...favoriteRecipe, recipe]);
    } else {
      const updatedRecipes = favoriteRecipe.filter(
        (recipe) => recipe.id !== meals[0].idMeal,
      );
      setFavoriteRecipe(updatedRecipes);
    }
  };

  return (
    <div data-testid="meal-details">
      <button onClick={ handleShareClick } type="button" data-testid="share-btn">
        <img src={ shareIcon } alt="Shared Icon" />
      </button>
      {copied && <p>Link copied!</p>}
      <button onClick={ handleFavoriteClick } type="button" data-testid="favorite-btn">
        <img src={ !isFavorite ? favoriteIcon : checkFavoriteIcon } alt="Shared Icon" />
      </button>
      {
        meals.map((meal) => (
          <div key={ meal.idMeal }>
            <img
              width="300px"
              data-testid="recipe-photo"
              src={ meal.strMealThumb }
              alt={ meal.strMeal }
            />
            <h3 data-testid="recipe-title">{ meal.strMeal }</h3>
            <h4 data-testid="recipe-category">{ meal.strCategory }</h4>
            <h5>Ingredients</h5>
            <ul>
              {
                ingredients.map((ingredient, index) => (
                  <li
                    data-testid={ `${index}-ingredient-name-and-measure` }
                    key={ index }
                  >
                    { ingredient }
                  </li>
                ))
              }
            </ul>
            <p data-testid="instructions">{ meal.strInstructions }</p>
            <iframe
              data-testid="video"
              title={ meal.strMeal }
              width="300"
              height="150"
              src={ meal.strYoutube.replace('watch?v=', 'embed/') }
            />
          </div>
        ))
      }
      <SlideDrinks />
    </div>
  );
}
