import PropTypes from 'prop-types';
import { useEffect, useState } from 'react';
import FinishButton from './FinishButton';
import '../styles/RecipeIngredients.css';

function RecipeIngredients({ recipe }) {
  const ingredients = [];
  const measures = [];
  const [selectedIngredients, setSelectedIngredients] = useState([]);
  const [isChecked, setIsCheked] = useState(false);
  const [isFinish, setIsFinish] = useState(false);

  useEffect(() => {
    let storageRecipes = JSON.parse(localStorage.getItem('inProgressRecipes'));
    if (storageRecipes === null) storageRecipes = [];
    setSelectedIngredients(storageRecipes);
  }, []);

  useEffect(() => {
    if (ingredients.length === selectedIngredients.length) {
      setIsFinish(true);
    } else {
      setIsFinish(false);
    }
  }, [selectedIngredients, ingredients.length]);

  function handleCheck(target, ingredient) {
    const { checked } = target;
    if (selectedIngredients.includes(ingredient)) {
      setSelectedIngredients(
        selectedIngredients.filter((item) => item !== ingredient),
      );
      localStorage.setItem(
        'inProgressRecipes',
        JSON.stringify(
          selectedIngredients.filter((item) => item !== ingredient),
        ),
      );
    } else {
      setSelectedIngredients([...selectedIngredients, ingredient]);
      localStorage.setItem(
        'inProgressRecipes',
        JSON.stringify([...selectedIngredients, ingredient]),
      );
    }

    if (checked) {
      setIsCheked(true);
    } else {
      setIsCheked(false);
    }
  }

  Object.keys(recipe).forEach((key) => {
    if (key.startsWith('strIngredient')) {
      const ingredient = recipe[key];
      if (ingredient !== '' && ingredient !== null) {
        ingredients.push(ingredient);
      }
    }
    if (key.startsWith('strMeasure')) {
      let measure = recipe[key];
      if (measure == null) {
        measure = '';
      }
      measures.push(measure);
    }
  });

  return (
    <div>
      {ingredients.map((ingredient, index) => (
        <div
          key={ index }
          data-testid={ `${index}-ingredient-step` }
        >
          <label
            htmlFor={ ingredient }
          >
            <p className={ isChecked ? 'select' : '' }>
              {`${measures[index]} ${ingredient}`}
            </p>
            <input
              type="checkbox"
              id={ ingredient }
              name={ ingredient }
              onChange={ ({ target }) => handleCheck(target, ingredient) }
              checked={ selectedIngredients.includes(ingredient) }
            />
          </label>
        </div>
      ))}
      <FinishButton isFinish={ isFinish } recipe={ recipe } />
    </div>
  );
}

RecipeIngredients.propTypes = {
  recipe: PropTypes.shape({}).isRequired,
};

export default RecipeIngredients;
