import Slider from 'react-slick';
import PropTypes from 'prop-types';
import '../styles/Carousel.css';

function CarouselDrinks({ items }) {
  const settings = {
    dots: false,
    arrows: true,
    infinite: false,
    speed: 500,
    slidesToShow: 2,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 1,
        },
      },
    ],
  };

  return (
    <section className="recommend">
      <Slider { ...settings }>
        <h4>Recommendations</h4>
        {items.map((item) => (
          <div key={ item.idDrink }>
            <img className="img-slide" src={ item.strDrinkThumb } alt={ item.strDrink } />
            <h5>{item.strDrink}</h5>
          </div>
        ))}
      </Slider>
    </section>
  );
}

CarouselDrinks.propTypes = {
  items: PropTypes.arrayOf(),
}.isRequired;

export default CarouselDrinks;
