import React from 'react';
import PropTypes from 'prop-types';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import '../styles/Slider.css';

const settings = {
  arrows: true,
  slidesToShow: 3,
  slidesToScroll: 1,
  infinite: false,
};

function SlideDrinks({ items }) {
  return (
    <Slider { ...settings }>
      <div className="slick-slider">
        {items.map((slide) => (
          <div className="slick-slider__slide" key={ slide.idDrink }>
            <img width="80px" src={ slide.strDrinkThumb } alt={ slide.strDrink } />
            <h5>{ slide.strDrink }</h5>
          </div>
        ))}
      </div>
    </Slider>
  );
}

SlideDrinks.propTypes = {
  items: PropTypes.arrayOf().isRequired,
};
export default SlideDrinks;
