import React from 'react';
import Slider from 'react-slick';
import PropTypes from 'prop-types';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const settings = {
  dots: true,
  infinite: false,
  speed: 300,
  slidesToShow: 2,
  slidesToScroll: 1,
  responsive: [
    {
      breakpoint: 1024,
      settings: {
        slidesToShow: 3,
        slidesToScroll: 1,
      },
    },
    {
      breakpoint: 800,
      settings: {
        slidesToShow: 2,
        slidesToScroll: 1,
      },
    },
    {
      breakpoint: 500,
      settings: {
        slidesToShow: 1,
        slidesToScroll: 1,
      },
    },
  ],
};

function Carousel({ slides }) {
  return (
    <Slider { ...settings }>
      <div className="slick-slider">
        {slides.map((slide) => (
          <div className="slick-slider__slide" key={ slide.idDrink }>
            <img width="80px" src={ slide.strDrinkThumb } alt={ slide.strDrink } />
            <h5>{ slide.strDrink }</h5>
          </div>
        ))}
      </div>
    </Slider>
  );
}

Carousel.propTypes = {
  slides: PropTypes.arrayOf().isRequired,
};

export default Carousel;
