import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import '~slick-carousel/slick/slick.css';
import '~slick-carousel/slick/slick-theme.css';

export default function RecipesDetailsDrinkss() {
  const { id } = useParams();
  const [drinks, setDrinks] = useState([]);
  const [recommendation, setRecommendation] = useState([]);

  useEffect(() => {
    const fetchDrinks = async () => {
      const response = await fetch(`https://www.thecocktaildb.com/api/json/v1/1/lookup.php?i=${id}`);
      const api = await response.json();
      const data = api.drinks;
      setDrinks(data);
    };
    fetchDrinks();

    const fetchRecommendation = async () => {
      const NUMBER = 6;
      const response = await fetch('https://www.themealdb.com/api/json/v1/1/search.php?s=');
      const api = await response.json();
      const data = api.meals;
      setRecommendation(data.slice(0, NUMBER));
    };
    fetchRecommendation();
  }, [setDrinks, setRecommendation, id]);

  const renderIngredients = () => {
    const NUMBER = 13;
    const ingredients = [];

    for (let index = 1; index <= NUMBER; index += 1) {
      if (drinks[0][`strIngredient${index}`]) {
        ingredients.push(
          `${drinks[0][`strMeasure${index}`]} ${drinks[0][`strIngredient${index}`]}`,
        );
      }
    }
    return ingredients.map((ingredient, index) => (
      <li
        data-testid={ `${index}-ingredient-name-and-measure` }
        key={ index }
      >
        { ingredient }

      </li>
    ));
  };

  return (
    <main>
      {
        drinks.map((drink) => (
          <div key={ drink.idDrink }>
            <img
              data-testid="recipe-photo"
              src={ drink.strDrinkThumb }
              alt={ drink.strDrink }
            />
            <h3 data-testid="recipe-title">{ drink.strDrink }</h3>
            <h4 data-testid="recipe-category">{ drink.strAlcoholic }</h4>
            <p />
            <h5>Ingredients</h5>
            <ul>{ renderIngredients() }</ul>
            <p data-testid="instructions">{ drink.strInstructions }</p>
          </div>
        ))
      }
    </main>
  );
}
